catalog_script_client.script:
function onLoad() {
   //DEPARTMENT SEARCH MESSAGE
   g_form.showErrorBox('u_department','Use * to search - Ex: *02645',true);

	//DAYS WORKED MESSAGE
	g_form.showErrorBox('u_days_worked','Mon, Tues, etc.',true);

	//HAVE YOU BEEN TO ANY OF THE FOLLOWING MESSAGE
   g_form.showErrorBox('u_staff_where_list','Please select all that apply',true);

	//CLOSE CONTACT LIST MESSAGE
   g_form.showErrorBox('u_close_contact_yesno', 'Close contact means within 6 feet for 15 consecutive minutes or more and either person was not wearing a mask. You must contact these staff immediately and send them home pending test result or a doctors release.  You must also create a case for each staff member. ');


	//LIMITED CONTACT LIST MESSAGE
	g_form.showErrorBox('u_limited_contact_yesno', 'Limited contact is staff who work in the same area and had some contact but did not come into close contact. ');

	//NEGATIVE PTO BALANCE MESSAGE
	 g_form.showErrorBox('u_neg_pto_bal', 'NFM will allow them to maintain a negative PTO balance of up to 40 hours. ');
}
